        console.log('spec', builderRef.current?.spec)
        console.log('vseed', builderRef.current?.vseed)
        console.log('advancedVSeed', builderRef.current?.advancedVSeed)
        console.groupEnd()
      }}
    >
      <div ref={ref} style={{ height: 300, width: '100%' }}></div>
    </div>
  )
}

export default Demo
